mapper.select:
SELECT ID, PASSWORD1, MEM_TYPE, NAME FROM ( SELECT MEM_ID AS ID, PASSWORD1, MEMBER_DIVISION AS MEM_TYPE, NAME FROM MEMBER
UNION all
SELECT PSMEM_ID AS ID, PASSWORD1, MEMBER_DIVISION AS MEM_TYPE, NAME FROM PETSITTER ) WHERE ID = #{ID}

SELECT ID, NAME, PHONE FROM ( SELECT MEM_ID AS ID, NAME, PHONE FROM MEMBER
UNION all
SELECT PSMEM_ID AS ID, NAME, PHONE FROM PETSITTER ) WHERE NAME = #{ID_NAME} AND PHONE = #{ID_PHONE}

SELECT PASSWORD1, ID, BIRTHDAY, PHONE FROM ( SELECT PASSWORD1, MEM_ID AS ID, BIRTHDAY, PHONE FROM MEMBER
UNION all
SELECT PASSWORD1, PSMEM_ID AS ID, BIRTHDAY, PHONE FROM PETSITTER ) WHERE ID = #{PW_ID} AND BIRTHDAY = #{PW_BIRTHDAY} AND PHONE = #{PW_PHONE}
