mapper.select:
SELECT ID, PASSWORD1 FROM ( SELECT MEM_ID AS ID, PASSWORD1 FROM MEMBER
UNION all
SELECT PSMEM_ID AS ID, PASSWORD1 FROM PETSITTER ) WHERE ID = #{ID} AND PASSWORD1 = #{PASSWORD1}

SELECT ID, NAME, PHONE FROM ( SELECT MEM_ID AS ID, NAME, PHONE FROM MEMBER
UNION all
SELECT PSMEM_ID AS ID, NAME, PHONE FROM PETSITTER ) WHERE NAME = #{NAME} AND PHONE = #{PHONE}

SELECT PASSWORD1, ID, BIRTHDAY, PHONE FROM ( SELECT PASSWORD1, MEM_ID AS ID, BIRTHDAY, PHONE FROM MEMBER
UNION all
SELECT PASSWORD1, PSMEM_ID AS ID, BIRTHDAY, PHONE FROM PETSITTER ) WHERE ID = #{ID} AND BIRTHDAY = #{BIRTHDAY} AND PHONE = #{PHONE}

SELECT ID FROM ( SELECT MEM_ID AS ID FROM MEMBER
UNION all
SELECT PSMEM_ID AS ID FROM PETSITTER ) WHERE ID = #{ID}

SELECT ID, PASSWORD1 FROM ( SELECT MEM_ID AS ID, PASSWORD1 FROM MEMBER
UNION all
SELECT PSMEM_ID AS ID, PASSWORD1 FROM PETSITTER ) WHERE ID = #{ID} AND PASSWORD1 = #{PASSWORD1}
