mapper.select:
SELECT ID FROM ( SELECT MEM_ID AS ID FROM MEMBER
UNION all
SELECT PSMEM_ID AS ID FROM PETSITTER ) WHERE ID = #{ID}

mapper.update:
UPDATE PETSITTER_ADD SET PSMEM_CONSIGNMENT_ZIPCODE = #{PSMEM_CONSIGNMENT_ZIPCODE}, PSMEM_CONSIGNMENT_ADDRESS1 = #{PSMEM_CONSIGNMENT_ADDRESS1}, PSMEM_CONSIGNMENT_ADDRESS2 = #{PSMEM_CONSIGNMENT_ADDRESS2}, PSMEM_CONSIGNMENT_ADDRESS_ADD = #{PSMEM_CONSIGNMENT_ADDRESS_ADD}, PSMEM_INTRODUCE = #{PSMEM_INTRODUCE}
WHERE
PSMEM_ID = #{PSMEM_ID}
